// Home page component - Simplified Todo App
'use client';

import TodoPage from './todo-page';

const HomePage = () => {
  return (
    <TodoPage />
  );
};

export default HomePage;

// Simplified Todo App Page - Original functionality restored
'use client';

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/input';
import { Modal } from '@/components/ui/modal';
import { TaskFormData } from '@/types';
import { useTask } from '@/hooks/use-task';
import { LoadingSpinner } from '@/components/common/loading-spinner';
import { Alert } from '@/components/common/alert';
import { PlusCircle, CalendarIcon, FlagIcon } from 'lucide-react';

const TodoPage = () => {
  const {
    tasks,
    loading,
    error,
    createTask,
    toggleTaskCompletion
  } = useTask();

  const [newTask, setNewTask] = useState<Omit<TaskFormData, 'completed'>>({
    title: '',
    description: '',
    dueDate: null,
    priority: 'medium',
    category: '',
    tags: []
  });

  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleInputChange = (field: keyof Omit<TaskFormData, 'completed'>, value: any) => {
    setNewTask(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleCreateTask = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!newTask.title.trim()) {
      return;
    }

    await createTask({
      ...newTask,
      completed: false
    });

    // Reset form
    setNewTask({
      title: '',
      description: '',
      dueDate: null,
      priority: 'medium',
      category: '',
      tags: []
    });

    // Close modal after creating task
    setIsModalOpen(false);
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold tracking-tight flex items-center">
            <span className="mr-2">📋</span> My Tasks
          </h1>
          <p className="text-muted-foreground mt-2">Manage your tasks and stay productive</p>
        </div>
        <Button className="mt-4 md:mt-0" onClick={() => setIsModalOpen(true)}>
          <PlusCircle className="mr-2 h-4 w-4" />
          Create Task
        </Button>
      </div>

      {error && <Alert type="error" message={error} />}

      {/* Task Creation Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} title="Create New Task">
        <form onSubmit={handleCreateTask} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="modal-title">Task Title</Label>
            <Input
              id="modal-title"
              placeholder="What needs to be done?"
              value={newTask.title}
              onChange={(e) => handleInputChange('title', e.target.value)}
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="modal-description">Description</Label>
            <Input
              id="modal-description"
              placeholder="Add details..."
              value={newTask.description}
              onChange={(e) => handleInputChange('description', e.target.value)}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="modal-dueDate">Due Date</Label>
              <div className="relative">
                <Input
                  id="modal-dueDate"
                  type="date"
                  value={newTask.dueDate ? new Date(newTask.dueDate).toISOString().split('T')[0] : ''}
                  onChange={(e) => handleInputChange('dueDate', e.target.value ? new Date(e.target.value) : null)}
                  className="pl-9"
                />
                <CalendarIcon className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="modal-priority">Priority</Label>
              <select
                id="modal-priority"
                value={newTask.priority}
                onChange={(e) => handleInputChange('priority', e.target.value as 'low' | 'medium' | 'high')}
                className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
              >
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
              </select>
            </div>
          </div>

          <div className="flex space-x-2 pt-4">
            <Button type="submit" className="flex-1">
              <PlusCircle className="mr-2 h-4 w-4" />
              Create Task
            </Button>
            <Button type="button" variant="outline" className="flex-1" onClick={() => setIsModalOpen(false)}>
              Cancel
            </Button>
          </div>
        </form>
      </Modal>

      {/* Task List */}
      <Card>
        <CardHeader>
          <CardTitle>Your Tasks</CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex justify-center py-8">
              <LoadingSpinner size="md" label="Loading tasks..." />
            </div>
          ) : tasks.length === 0 ? (
            <div className="text-center py-8">
              <div className="mx-auto h-24 w-24 rounded-full bg-muted flex items-center justify-center">
                <FlagIcon className="h-12 w-12 text-muted-foreground" />
              </div>
              <h3 className="mt-4 text-lg font-medium">No tasks yet</h3>
              <p className="text-muted-foreground">Get started by creating a new task</p>
            </div>
          ) : (
            <ul className="space-y-4">
              {tasks.map(task => (
                <li key={task.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-muted/50 transition-colors">
                  <div className="flex items-center space-x-4">
                    <input
                      type="checkbox"
                      checked={task.completed}
                      onChange={() => toggleTaskCompletion(task.id)}
                      className="h-5 w-5 rounded border-input text-primary focus:ring-primary"
                    />
                    <div className="grid gap-1">
                      <span className={`text-sm font-medium ${task.completed ? 'line-through text-muted-foreground' : 'text-foreground'}`}>
                        {task.title}
                      </span>
                      {task.description && (
                        <span className="text-xs text-muted-foreground line-clamp-1">
                          {task.description}
                        </span>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className={`inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-semibold ${
                      task.priority === 'high' ? 'bg-destructive text-destructive-foreground' :
                      task.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-green-100 text-green-800'
                    }`}>
                      {task.priority}
                    </span>
                    {task.dueDate && (
                      <span className="text-xs text-muted-foreground">
                        {new Date(task.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </li>
              ))}
            </ul>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default TodoPage;

